r -->\n</div>\n<!-- /.wrapper -->\n</body>\n</html>\n',0,'a:0:{}',0,''),(9,1,0,'All Downloads','',0,0,'',0,'',0,'a:0:{}',0,''),(3,1,0,'Typography','',0,0,'',0,'',0,'a:0:{}',0,''),(4,1,0,'Colour','',0,0,'',0,'',0,'a:0:{}',0,''),(5,1,0,'Logo','',0,0,'',0,'',0,'a:0:{}',0,''),(6,1,0,'Iconography','',0,0,'',0,'',0,'a:0:{}',0,''),(7,1,0,'Images And Videos','',0,0,'',0,'',0,'a:0:{}',0,''),(8,1,0,'Templates','',0,0,'',0,'',0,'a:0:{}',0,''),(2,1,0,'Principles','',0,0,'',0,'',0,'a:0:{}',0,'');
